import React from 'react';
import ReactDOM from 'react-dom';

const AddUserButton = React.memo(function() {
    return( 
        <div className="addUserContainer">
            <div className="addUser">
                <i className="fa fa-user-plus" aria-hidden="true"></i>
            </div>
        </div>
    );
});

const UserImage = React.memo(function({src}) {
    return (
        <div className="userImage">
            <img src = "assets/john-paul.jpeg" alt="John Paul" height="100px" width="100px" />
        </div>
    );
})

function UserCard({user}) {
    return (
        <section className="flexItems">
            <div className="userEdit">
                <i className="fa fa-pencil-square-o" aria-hidden="true"></i>
            </div>
            <UserImage src={user.imageUrl} />
            <div className="userName">
                {user.name}
            </div>

            <div className="userDesignation">
                {user.designation}
            </div>
            <hr color='#404040' className="lineBreaks" />

            <div className="userInfo userLocation">
                <i className="fa fa-map-marker"></i> &nbsp &nbsp New York, USA
            </div>

            <div className="userInfo userMail">
                <i className="fa fa-envelope" aria-hidden="true"></i> &nbsp john.paul@company.com
            </div>

            <div className="userInfo userPhone">
                <i className="fa fa-phone" aria-hidden="true"></i> &nbsp +19998645408
            </div>
        </section>
    );
}

function UserContainer({users}) {
    return (
        <div id="flexContainer">
            {users.map((user) => <UserCard user={user} key={user.id}/>)}
        </div>
    );
}

function Team(props) {

    let users = fetchUsers();
    return (
        <section className="board">
            <AddUserButton />
            <UserContainer users={users} />
        </section>
    );
}

function fetchUsers() {
    localStorage.clear();
    let users = JSON.parse(localStorage.getItem('users')) || [];
    if(users.length == 0)
    {
        let newUser1 = {};
        newUser1.id = 0;
        newUser1.imageUrl = "https://www.pavilionweb.com/wp-content/uploads/2017/03/man-300x300.png";
        newUser1.name = "Dhruv Patel";
        newUser1.designation = "project head";
        newUser1.location = "Mumbai, India";
        newUser1.email = "dhruv.patel@comapnay.com";
        newUser1.phone = "+917878345672";
        newUser1.tasks = [0,1];
        users.push(newUser1);

        let newUser2 = {};
        newUser2.id = 1;
        newUser2.imageUrl = "assets/john-paul.jpeg";
        newUser2.name = "John Paul";
        newUser2.designation = "project manager";
        newUser2.location = "New York, USA";
        newUser2.email = "john.paul@comapnay.com";
        newUser2.phone = "+19998645408";
        newUser2.tasks = [2,3];
        users.push(newUser2);

        commitUsers(users);
    }
    return users;
}

function commitUsers(users) {
    localStorage.setItem('users',JSON.stringify(users));
}

export default Team;
