import { useState } from "react";
import { act } from "react-dom/test-utils";
import Tasks from "../Tasks";
import actions from "./actions";

let users = [];
let newUser1 = {};
newUser1.id = 0;
newUser1.imageUrl = "https://www.pavilionweb.com/wp-content/uploads/2017/03/man-300x300.png";
newUser1.name = "Dhruv Patel";
newUser1.designation = "project head";
newUser1.location = "Mumbai, India";
newUser1.email = "dhruv.patel@comapnay.com";
newUser1.phone = "917878345672";
newUser1.tasks = [0];
users.push(newUser1);

let newUser2 = {};
newUser2.id = 1;
newUser2.imageUrl = "assets/john-paul.jpeg";
newUser2.name = "John Paul";
newUser2.designation = "project manager";
newUser2.location = "New York, USA";
newUser2.email = "john.paul@comapnay.com";
newUser2.phone = "19998645408";
newUser2.tasks = [1];
users.push(newUser2);


const rootReducer = (state = {users: users,tasks: []},action) => {
    switch (action.type) {
        case "ADD_USER" :
            return (
                {
                    ...state,
                    users: [...state.users,action.payload],
                }
            )

        case "REMOVE_USER" :
            console.log(action);
            return (
                {
                    users: state.users.filter(user => user.id != action.payload),
                    tasks: state.tasks.filter(task => task.assignee != action.payload),
                }
            )

        case "ADD_TASK" : {
            let user = state.users.find(user => user.id == action.payload.assignee);
            user.tasks.push(action.payload.task.taskId);
            return (
                {
                    users: state.users,
                    tasks: [...state.tasks,action.payload.task],
                }
            )
        }

        case "REMOVE_TASK" : {
            let task = state.tasks.find(task => task.taskId == action.payload.taskId);
            let user = state.users.find(user => user.id == task.assignee);
            user.tasks = user.tasks.filter(taskId => taskId != action.payload.taskId);
            return (
                {
                    users: state.users,
                    tasks: state.tasks.filter(task => task.taskId != action.payload.taskId),
                }
            )
        }

        case "UPDATE_USER" : {
            let ind = state.users.findIndex(user => user.id == action.payload.id)
            return (
                {
                    ...state,
                    users: [...state.users.slice(0,ind),action.payload.user,...state.users.slice(ind+1)],
                }
            )
        }

        case "UPDATE_TASK" : {
            let ind = state.tasks.findIndex(task=> task.taskID == action.payload.taskId);
            if(state.task[ind].assignee == action.payload.task.assignee) {
                return (
                    {
                        ...state,
                        tasks: [...state.tasks.slice(0,ind),action.payload.task,...state.tasks.slice(ind+1)]
                    }
                )
            }
            else {
                let user = state.users.find(user => user.id == state.tasks[ind].assignee);
                user.tasks = user.tasks.filter(taskId => taskId != action.payload.taskId);
                let newUser = state.users.find(user => user.id == action.payload.task.taskID);
                newUser.tasks = [...newUser.tasks, action.payload.task.taskID];
                return (
                    {
                        users: state.users,
                        tasks: [...state.tasks.slice(0,ind),action.payload.task,...state.tasks.slice(ind+1)]
                    }
                )
            }
        }

        default : return state;
    }
}

export default rootReducer;
