const rootReducer = (state={},action) => {
    switch (action.type) {
        case "ADD_USER" :
            return (
                {
                    ...state,
                    users: [...state.users,action.payload],
                }
            )
        case "REMOVE_USER" :
            return (
                {
                    users: state.users.filter(user => user.id != action.payload.id)
                }
            )

    }
}
export default rootReducer;
