import React, {useEffect, useState} from 'react';
import ReactDOM from 'react-dom';
import { NavLink } from 'react-router-dom';

function Navbutton({route,isActive,handleClick}) {

    const handleClickCallback = function () {
        console.log("tasks clicked");
        handleClick();
    }

    let className = "navButton " + route;
    if(isActive) {
        className += " active";
    }
    return (
        <div className={className} onClick={handleClickCallback}>{route.toUpperCase()}</div>
    )
}


function Navbar(props) {

    const [task,setTask] = useState(false);

    useEffect(() => {
        if(task) {
            window.location.href="/tasks";
        }
        else {
            window.location.href="/team";
        }
    })

    const activeTask = function() {
        window.location.href = "/tasks";
        setTask(true);
    }

    const activeUser = function() {
        window.location.href = "/team";
        setTask(false);
    }

    return (
        <div className="topnav">
            <Navbutton route={`tasks`} isActive={task} handleClick={activeTask}/>
            <Navbutton route={`team`} isActive={!task} handleClick={activeUser}/>
        </div>
    );
}

function Navigation(props) {
    return (
        <header className="header">
            <Navbar />
            <div id="projectName">
                <h1>McDonalds Website Project</h1>
            </div>
        </header>
    );
}

export default Navigation;
