const TaskImage = React.memo(function({src}) {
    return (
        <div className="taskImage">
            <img src = {src} height="160px" width="252px"/>
        </div>
    );
})

function TaskCard({task}) {
    return (
        <div className="card">
            <TaskImage src={src} />
            <div class="taskTitle">
                Create welcome page for restaurant!
            </div>
            <hr class="titleLineBreaks" />

            <div class="taskInfo">
                <span class="taskStatus">
                <i class="fa fa-square taskStatusSymbol" aria-hidden="true"></i> In-progress
                </span>

                <span class="taskDue">
                <i class="fa fa-clock-o" aria-hidden="true"></i> 20 DEC, 2021
                </span>

                <span>
                <i class="fa fa-check-square-o" aria-hidden="true"></i> 0/4
                </span>
            </div>
        </div>
    )
}

function UserColumn({userTasks,userName}) {
    return (
        <section className="board-list">
            <div className="tasksAdd">
                <i className="fa fa-plus-circle" aria-hidden="true"></i>
            </div>
            <div className="list-title">
                {userName}
            </div>
            {userTasks.map(task => <TaskCard task={task} key={task.taskId}/>)}
        </section>
    );
}

function TasksContainer({tasks,users}) {

    let taskMap = new Map();
    tasks.forEach(element => {
        taskMap.set(element.taskId,element);
    });

    return (
        <div className="board-lists">
            {users.map(function (user) {
                let userTasks = [];
                user.tasks.forEach(id => userTasks.push(taskMap.get(id)));
                return <UserColumn userTasks={userTasks} key={user.id} userName={user.name}/>
            })}
        </div>
    )
}

function Tasks(props) {
    let users = fetchUsers();
    let tasks = fetchTasks();
    return (
        <section className="board">
            <TasksContainer users={users} tasks={tasks} />
        </section>
    )
}

function fetchTasks() {
    let tasks = JSON.parse(localStorage.getItem('tasks')) || [];
    if(tasks.length == 0)
    {
        let newTask1 = {};
        newTask1.taskId = 0;
        newTask1.imageUrl = "assets/macd-first-page.jpeg";
        newTask1.title = "Create welcome page of the restaurant";
        newTask1.assignee = 0;
        newTask1.status = 0;
        newTask1.dueDate = "2020-12-25";
        newTask1.stages = {
            "Write HTML document" : false,
            "Add styling to the page using CSS" : false,
            "Add interaction using Javascript" : false,
            "Deploy the page" : false,
        };
        tasks.push(newTask1);

        let newTask2 = {};
        newTask2.taskId = 1;
        newTask2.imageUrl = "assets/default.png";
        newTask2.title = "create customer care utility";
        newTask2.assignee = 1;
        newTask2.status = 0;
        newTask2.dueDate = "2020-12-25";
        newTask2.stages = {
            "Build static layout" : false,
            "Add UI in the page" : false,
            "Create chatbot" : false,
            "Design ML model for chatbot" : false,
        };
        tasks.push(newTask2);
        commitTasks(tasks);
    }
    return tasks;
}

function commitTasks(tasks) {
    localStorage.setItem('tasks',JSON.stringify(tasks));
}

function fetchUsers() {
    let users = JSON.parse(localStorage.getItem('users'));
    return users;
}

function commitUsers(users) {
    localStorage.setItem('users',JSON.stringify(users));
}

export default Tasks;
